import { Component } from 'react'
import { Row, Container } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { loadState, saveState } from '../../localstorage/LocalStorage';
import { authenticationClient } from '../../network/clients';
import axios from "axios";


export class LoginComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            email: "",
            password: ""
        };
        this.handleChangeEmail = this.handleChangeEmail.bind(this);
        this.handleChangePassword = this.handleChangePassword.bind(this);
        this.sendLoginRequest = this.sendLoginRequest.bind(this);
    }

    handleChangeEmail(event) {
        this.setState({ email: event.target.value });
    }

    handleChangePassword(event) {
        this.setState({ password: event.target.value });
    }

    getToken(response) {
        console.log(response);
    }

    sendLoginRequest(event) {
        event.preventDefault();
        console.log(this.state);
        let email = this.state.email;
        let password = this.state.password;
        /*
        fetch(
            '/delivery/list/dispatcher/all',
            {
                method:'GET',
                mode: 'no-cors',
                header: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: null,
                referrerPolicy: 'no-referrer'
            }
        ).then(
            (response) => {
                if (response.ok) {
                    response.json().then(json => {
                        console.log(json);
                    });
                }
            }
        )

         */
        /*
        fetch(
            'http://192.168.68.123:10789/user/login',
            {
                method:'POST',
                mode: 'no-cors',
                header: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: {
                    "email": email,
                    "password": password
                },
            }
        ).then(
            (response) => {
                console.log(response)
                if (response.ok) {
                    response.json().then(json => {
                        console.log(json);
                    });
                }
            }
        )

        */
        const userObject = {
            email: email,
            password: password
        }
        axios.get("delivery/list/dispatcher/all")
            .then(
                (res) => {
                    console.log(res)
                    console.log(res.data)
                }
            )
            .catch(
                (error) => {
                    console.log(error)
                }
            )

        /*
        authenticationClient.post('/user/login', {
            "email": email,
            "password": password
        })
            .then((response) => {
                this.getToken(response);
            });

        saveState("login",true);
        console.log(loadState("login"));

         */

    }

    render() {
        return <Container >
            <Row className="justify-content-md-center mt-5" xs={6} md={2}>
                <Form onSubmit={this.sendLoginRequest}>
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" placeholder="Email" value={this.state.email} onChange={this.handleChangeEmail} />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" value={this.state.password} onChange={this.handleChangePassword} />
                    </Form.Group>
                    <div className="d-grid gap-2">
                        <Button variant="primary" type="submit" size="lg">
                            Submit
                        </Button>
                    </div>
                </Form>
            </Row>
        </Container>
    }
}
